<!DOCTYPE html><html><head>    <meta charset="UTF-8">    <title>Login Page</title>    <script src="    https://cdn.jsdelivr.net/npm/js-cookie@3.0.5/dist/js.cookie.min.js"></script>    <!-- 引入样式 -->    <link rel="stylesheet" href="https://unpkg.com/element-ui/lib/theme-chalk/index.css">    <!-- import Vue before Element -->    <script src="https://unpkg.com/vue@2/dist/vue.js"></script>    <!-- import JavaScript -->    <script src="https://unpkg.com/element-ui/lib/index.js"></script>    <style>        @supports (-webkit-mask: none) and (not (cater-color: #fff)) {            .login-container .el-input input {                color: #333333 !important;            }        }        /*.el-input {*/        /*    display: inline-block;*/        /*    height: 47px;*/        /*    width: 85%;*/        /*}*/        /*input {*/        /*    background: none;*/        /*    border: 0px;*/        /*/ / -webkit-appearance: none;*/        /*    border-radius: 0px;*/        /*    padding: 12px 5px 12px 15px;*/        /*    height: 47px;*/        /*    color: #333;*/        /*}*/        body {            background-image: url('/images/3.jpeg');            background-size: cover;            overflow: hidden;            position: relative;        }        .login-container {            height: 100vh;            width: 100%;        }        .login-form {            width: 600px;            max-width: 50%;            padding: 20px 10px 10px;            position: absolute;            left: 50%;            top: 50%;            transform: translate(-50%, -50%);            overflow: hidden;            background: rgba(255, 255, 255, 0.9);            border-radius: 6px;            box-shadow: 0 2px 12px 5px rgba(0, 0, 0, 0.1);        }        .tips {            font-size: 14px;            color: #fff;            margin-bottom: 10px;            text-align: center;        }        .title-container {            position: relative;        }        .title {            font-size: 26px;            color: #333333;            margin: 0 auto 60px auto;            text-align: center;            font-weight: bold;        }    </style></head><body><div id="app" class="login-container">    <div style="text-align: center">        <el-form                ref="loginForm"                :model="loginForm"                :rules="loginRules"                class="login-form"                auto-complete="on"        >            <div class="title-container">                <h3 class="title">风控SSO授权认证中心</h3>            </div>            <el-row type="flex" justify="center">                <el-col :span="18">                    <el-form-item prop="username">                        <el-input                                ref="username"                                v-model="loginForm.username"                                placeholder="请输入风控系统账户名"                                name="username"                                type="text"                                tabindex="1"                                auto-complete="on"                                clearable                                prefix-icon="el-icon-user"                        >                        </el-input>                    </el-form-item>                </el-col>            </el-row>            <el-row type="flex" justify="center">                <el-col :span="18">                    <el-form-item prop="password">                        <el-input                                :key="passwordType"                                ref="password"                                prefix-icon="el-icon-unlock"                                v-model="loginForm.password"                                :type="passwordType"                                placeholder="请输入风控系统密码"                                name="password"                                tabindex="2"                                auto-complete="on"                                show-password                                clearable                                @keyup.enter.native="handleLogin"                        />                    </el-form-item>                </el-col>            </el-row>            <el-button                    :loading="loading"                    type="primary"                    style="width: 50%; height: 50px; margin: 30px 0"                    @click.native.prevent="handleLogin"            >登录</el-button>        </el-form>    </div></div></body><script>  new Vue({    el: '#app',    components: {      'svg-icon': 'url:./components/svg-icon.vue',    },    data() {      const validateUsername = (rule, value, callback) => {        if (!value) {          callback(new Error('请输入账号'));        } else {          callback();        }      };      const validatePassword = (rule, value, callback) => {        if (value.length < 6) {          callback(new Error('请输入密码'));        } else {          callback();        }      };      return {        loginForm: {          username: '',          password: ''        },        loginRules: {          username: [            { required: true, trigger: 'blur', validator: validateUsername }          ],          password: [            { required: true, trigger: 'blur', validator: validatePassword }          ]        },        loading: false,        passwordType: 'password',        redirect: undefined,        showVerify: false      };    },    methods: {      handleLogin() {        this.loading = true;        this.$refs.loginForm.validate((valid) => {          if (valid) {            this.showVerify = true;            this.loading = false;            window.location.href = 'forward'          } else {            console.log('error submit!!');            this.loading = false;            return false;          }        });      },      onSuccess() {        this.showVerify = false;        this.$refs.slideblock.reset();      }    }  })</script></html>